import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link, Switch } from 'react-router-dom';
import PersonalInfo from './components/PersonalInfo';
import Address from './components/Address';
import EducationalInfo from './components/EducationalInfo';
import './App.css';
import axios from 'axios';

const SubmissionSuccess = () => (
  <div>
    <h2>Your form has been submitted successfully!</h2>
    <p>Thank you for submitting the form.</p>
    {/* You can provide additional details or links here */}
  </div>
);

const App = () => {
  const [personalInfo, setPersonalInfo] = useState(null);
  const [addressInfo, setAddressInfo] = useState(null);
  const [educationalInfo, setEducationalInfo] = useState(null);

  const handleSubmitAllData = () => {
    // Check if all data is available
    if (personalInfo && addressInfo && educationalInfo) {
      // Combine all data
      const allData = {
        personalInfo,
        addressInfo,
        educationalInfo,
      };

      // Send the combined data to the server
      axios.post('http://localhost:3001/api/allData', allData)
        .then(response => {
          console.log('All data submitted successfully:', response.data);
          // Use React Router to navigate to the success page
          history.push('/success');
        })
        .catch(error => {
          console.error('Error submitting all data:', error);
        });
    } else {
      console.error('Incomplete data. Please fill in all sections.');
    }
  };

  return (
    <Router>
      <div className="App">
        <Switch>
          <Route path="/success" component={SubmissionSuccess} />
          <Route path="/">
            <PersonalInfo onSubmit={setPersonalInfo} />
            <Address onSubmit={setAddressInfo} />
            <EducationalInfo onSubmit={setEducationalInfo} />
            <button onClick={handleSubmitAllData}>Submit All Data</button>
          </Route>
        </Switch>
      </div>
    </Router>
  );
};

export default App;
