import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Link, Navigate } from 'react-router-dom';
import PersonalInfo from './components/PersonalInfo';
import Address from './components/Address';
import EducationalInfo from './components/EducationalInfo';
import './App.css';
import axios from 'axios';

const SubmissionSuccess = () => (
  <div>
    <h2>Your form has been submitted successfully!</h2>
    <p>Thank you for submitting the form.</p>
    {/* You can provide additional details or links here */}
  </div>
);

const App = () => {
  const [personalInfo, setPersonalInfo] = useState(null);
  const [addressInfo, setAddressInfo] = useState(null);
  const [educationalInfo, setEducationalInfo] = useState(null);

  const handlePersonalInfoSubmit = (data) => {
    setPersonalInfo(data);
    // Use React Router to navigate to the next page (Address)
    // You can replace the window.location.href with a more appropriate navigation mechanism
    window.location.href = '/address';
  };

  const handleAddressSubmit = (data) => {
    setAddressInfo(data);
    // Use React Router to navigate to the next page (EducationalInfo)
    // You can replace the window.location.href with a more appropriate navigation mechanism
    window.location.href = '/educationalInfo';
  };

  const handleEducationalInfoSubmit = (data) => {
    setEducationalInfo(data);
    // Assuming you want to navigate to the submit page after educational info
    // You can replace the window.location.href with a more appropriate navigation mechanism
    window.location.href = '/submit';
  };

  const handleSubmitAllData = () => {
    // Submit all data to the server and navigate to the success page
    // (as in the previous response)
  };

  return (
    <Router>
      <div className="App">
        <Routes>
          <Route
            path="/"
            element={<PersonalInfo onSubmit={handlePersonalInfoSubmit} />}
          />
          <Route
            path="/address"
            element={<Address onSubmit={handleAddressSubmit} />}
          />
          <Route
            path="/educationalInfo"
            element={<EducationalInfo onSubmit={handleEducationalInfoSubmit} />}
          />
          <Route
            path="/submit"
            element={<button onClick={handleSubmitAllData}>Submit All Data</button>}
          />
          <Route path="/success" element={<SubmissionSuccess />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
