import React, { useState } from "react";
import "./App.css";
import "./Button.css";
import PersonalInfo from "./components/PersonalInfo";
import Address from "./components/Address";
import EducationalInfo from "./components/EducationalInfo";


const App = () => {
  const [formData, setFormData] = useState({
    personalInfo: {
      firstName: "",
      middleName: "",
      lastName: "",
      email: "",
      mobileNo: "",
      gender: "",
      degree: "",
      dob: "",
      hasDisable: "",
    },
    address: {
      presentAddress: "",
      streetAddress: "",
      streetAddressLineTwo: "",
      city: "",
      state: "",
      zipCode: "",
      country: "",
    },
    educationalInfo: {
      hscSchoolName: "",
      hscMarks: "",
      sscSchoolName: "",
      sscMarks: "",
      ugCollegeName: "",
      ugMarks: "",
      familyIncome: "",
      hasScholarship: false,
      isPhysicallyDisabled: false,
    },
  });
  const [termsAccepted, setTermsAccepted] = useState(false);
  const [submitButtonText, setSubmitButtonText] = useState("Submit All Data");


  const handleFormData = (data, componentName) => {
    setFormData((prevData) => ({
      ...prevData,
      [componentName]: data,

    }));
  };


  const handleTermsChange = () => {
    setTermsAccepted((prevValue) => !prevValue);
  };

  const handleFinalSubmit = async () => {
    const {
      firstName,
      middleName,
      lastName,
      email,
      mobileNo,
      gender,
      degree,
      dob,
      hasDisable,
      presentAddress,
      streetAddress,
      streetAddressLineTwo,
      city,
      state,
      zipCode,
      country,
      hscSchoolName,
      hscMarks,
      sscSchoolName,
      sscMarks,
      ugCollegeName,
      ugMarks,
      familyIncome,
    } = formData;

    const allFieldsFilled =
      firstName !== "" &&
      middleName !== "" &&
      lastName !== "" &&
      email !== "" &&
      mobileNo !== "" &&
      gender !== "" &&
      degree !== "" &&
      dob !== "" &&
      hasDisable !== "" &&
      presentAddress !== "" &&
      streetAddress !== "" &&
      streetAddressLineTwo !== "" &&
      city !== "" &&
      state !== "" &&
      zipCode !== "" &&
      country !== "" &&
      hscSchoolName !== "" &&
      hscMarks !== "" &&
      sscSchoolName !== "" &&
      sscMarks !== "" &&
      ugCollegeName !== "" &&
      ugMarks !== "" &&
      familyIncome !== "";

    if (!allFieldsFilled) {
      alert("Please fill in all required fields!");
      return;
    }

    if (!termsAccepted) {
      alert("Please accept the terms and conditions!");
      return;
    }

    try {
      const response = await fetch('/api/registration', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        console.log('Data successfully sent to the server');
        setSubmitButtonText("Your request has been submitted. Thank you!");
      } else {
        console.error('Failed to send data to the server');
      }
    } catch (error) {
      console.error('Error:', error);
    }
  };



  return (
    <div className="app">
      <PersonalInfo onSubmit={(data) => handleFormData(data, "personalInfo")} />
      <Address onSubmit={(data) => handleFormData(data, "address")} />
      <EducationalInfo onSubmit={(data) => handleFormData(data, "educationalInfo")}
      />
      <div className="s-btn">
        <label className="checkbox-label" htmlFor="termsCheckbox">
          <input
            id="termsCheckbox"
            type="checkbox"
            checked={termsAccepted}
            onChange={handleTermsChange}
          />
          <span className="custom-checkbox"></span>
          <span className="checkbox-text">Accept Terms and Conditions</span>
        </label>
        <button onClick={handleFinalSubmit}>{submitButtonText}</button>
      </div>
    </div>
  );
};

export default App;
