import React, { useState } from 'react';
import PersonalInfo from './components/PersonalInfo';
import Address from './components/Address';
import EducationalInfo from './components/EducationalInfo';

const App = () => {
  const [personalInfoData, setPersonalInfoData] = useState({});
  const [addressData, setAddressData] = useState({});
  const [educationalInfoData, setEducationalInfoData] = useState({});
  const [formSubmitted, setFormSubmitted] = useState(false);

  const handlePersonalInfoSubmit = (data) => {
    setPersonalInfoData(data);
  };

  const handleAddressSubmit = (data) => {
    setAddressData(data);
  };

  const handleEducationalInfoSubmit = (data) => {
    setEducationalInfoData(data);
  };

  const handleFinalSubmit = () => {
    // Check if all required fields are filled
    if (
      Object.values(personalInfoData).every((value) => value !== '') &&
      Object.values(addressData).every((value) => value !== '') &&
      Object.values(educationalInfoData).every((value) => value !== '')
    ) {
      // Here, you can pass the data to the database and perform the reset
      console.log('All data filled. Ready for final submission.');
      setPersonalInfoData({});
      setAddressData({});
      setEducationalInfoData({});
      setFormSubmitted(true);
    } else {
      alert('Please fill all required fields in all sections.');
    }
  };

  return (
    <div className='App'>
      <PersonalInfo onSubmit={handlePersonalInfoSubmit} />
      <Address onSubmit={handleAddressSubmit} />
      <EducationalInfo onSubmit={handleEducationalInfoSubmit} />
     <div cl> <button id='fsb' onClick={handleFinalSubmit} disabled={!formSubmitted}>
        Submit All Data
      </button>
      </div> 
    </div>
  );
};

export default App;
