import React, { useState } from 'react';
import PersonalInfo from './components/PersonalInfo';
import Address from './components/Address';
import EducationalInfo from './components/EducationalInfo';
import './App.css';
import axios from 'axios';

const App = () => {
  const [personalInfo, setPersonalInfo] = useState(null);
  const [addressInfo, setAddressInfo] = useState(null);
  const [educationalInfo, setEducationalInfo] = useState(null);
  const [isDataSubmitted, setIsDataSubmitted] = useState(false);

  const handlePersonalInfoSubmit = (data) => {
    setPersonalInfo(data);
  };

  const handleAddressSubmit = (data) => {
    setAddressInfo(data);
  };

  const handleEducationalInfoSubmit = (data) => {
    setEducationalInfo(data);
  };

  const handleSubmitAllData = () => {
    // Check if all data is available
    if (personalInfo && addressInfo && educationalInfo) {
      // Combine all data
      const allData = {
        personalInfo,
        addressInfo,
        educationalInfo,
      };

      // Send the combined data to the server
      axios.post('http://localhost:3001/api/allData', allData)
        .then(response => {
          console.log('All data submitted successfully:', response.data);
          setIsDataSubmitted(true); // Set the flag to true after successful submission
        })
        .catch(error => {
          console.error('Error submitting all data:', error);
        });
    } else {
      console.error('Incomplete data. Please fill in all sections.');
    }
  };

  return (
    <div className="App">
      <PersonalInfo onSubmit={handlePersonalInfoSubmit} />
      <Address onSubmit={handleAddressSubmit} />
      <EducationalInfo onSubmit={handleEducationalInfoSubmit} />

      {/* Display the submit button only if data has not been submitted */}
      {!isDataSubmitted && (
        <button onClick={handleSubmitAllData}>Submit All Data</button>
      )}
    </div>
  );
};

export default App;
