import React, { useState } from 'react';
import axios from 'axios';
import PersonalInfo from './components/PersonalInfo';
import Address from './components/Address';
import EducationalInfo from './components/EducationalInfo';
import './App.css';

const App = () => {
  const [personalInfoData, setPersonalInfoData] = useState({});
  const [addressData, setAddressData] = useState({});
  const [educationalInfoData, setEducationalInfoData] = useState({});
  const [formSubmitted, setFormSubmitted] = useState(false);

  const handlePersonalInfoSubmit = (data) => {
    console.log('Personal Info submitted:', data);
    setPersonalInfoData((prevData) => ({ ...prevData, ...data }));
  };

  const handleAddressSubmit = (data) => {
    console.log('Address submitted:', data);
    setAddressData((prevData) => ({ ...prevData, ...data }));
  };

  const handleEducationalInfoSubmit = (data) => {
    console.log('Educational Info submitted:', data);
    setEducationalInfoData((prevData) => ({ ...prevData, ...data }));
  };


  const isPersonalInfoComplete = Object.values(personalInfoData).every((value) => value !== '');
  const isAddressComplete = Object.values(addressData).every((value) => value !== '');
  const isEducationalInfoComplete = Object.values(educationalInfoData).every((value) => value !== '');

 const allSectionsComplete = isPersonalInfoComplete && isAddressComplete && isEducationalInfoComplete;
 const [termsCheckboxChecked, setTermsCheckboxChecked] = useState(false);


  return (
    <div className="App">
      <PersonalInfo onSubmit={handlePersonalInfoSubmit} />
      <Address onSubmit={handleAddressSubmit} />
      <EducationalInfo onSubmit={handleEducationalInfoSubmit} />

      <div className="s-btn">
        <label className="checkbox-label" htmlFor="termsCheckbox">
          <input
  id="termsCheckbox"
  type="checkbox"
  checked={termsCheckboxChecked}
  onChange={(e) => setTermsCheckboxChecked(e.target.checked)}
/>
          <span className="custom-checkbox"></span>
          <span className="checkbox-text">Accept Terms and Conditions</span>
        </label>
        <button id="fsb"  disabled={!allSectionsComplete}>
          {formSubmitted ? 'Data Submitted!' : 'Submit All Data'}
        </button>
      </div>
    </div>
  );
};

export default App;
