import React, { useState } from "react";
import "./App.css";
import "./Button.css";
import PersonalInfo from "./components/PersonalInfo";
import Address from "./components/Address";
import EducationalInfo from "./components/EducationalInfo";


const App = () => {
  const [formData, setFormData] = useState({
    personalInfo: {
      firstName: "",
      middleName: "",
      lastName: "",
      email: "",
      mobileNo: "",
      gender: "",
      degree: "",
      dob: "",
      hasDisable: "",
    },
    address: {
      presentAddress: "",
      streetAddress: "",
      streetAddressLineTwo: "",
      city: "",
      state: "",
      zipCode: "",
      country: "",
    },
    educationalInfo: {
      hscSchoolName: "",
      hscMarks: "",
      sscSchoolName: "",
      sscMarks: "",
      ugCollegeName: "",
      ugMarks: "",
      familyIncome: "",
      hasScholarship: false,
      isPhysicallyDisabled: false,
    },
  });
  const [termsAccepted, setTermsAccepted] = useState(false);
  const [submitButtonText, setSubmitButtonText] = useState("Submit All Data");
  const [submitting, setSubmitting] = useState(false); // New state for submission status

  const handleFormData = (data, componentName) => {
    setFormData((prevData) => ({
      ...prevData,
      [componentName]: data,
    }));
  };


  const handleTermsChange = () => {
    setTermsAccepted((prevValue) => !prevValue);
  };

  const handleFinalSubmit = async () => {
    const { personalInfo, address, educationalInfo } = formData;

    const allFieldsFilled =
      Object.values(personalInfo).every((val) => val.trim() !== "") &&
      Object.values(address).every((val) => val.trim() !== "") &&
      Object.values(educationalInfo).every((val) => val !== "");

    if (!allFieldsFilled) {
      alert("Please fill in all required fields!");
      return;
    }

    if (!termsAccepted) {
      alert("Please accept the terms and conditions!");
      return;
    }




    try {
      setSubmitting(true); // Set submitting state to true during the API call
      const response = await fetch('/api/registration', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ formData }),
      });
      if (response.ok) {
        console.log('Data successfully sent to the server');
        setSubmitButtonText("Your request has been submitted. Thank you!");
        setFormData({  // Clear the form after successful submission
          personalInfo: { ...PersonalInfo }, // initialPersonalInfo contains empty strings for fields
          address: { ...Address },
          educationalInfo: { ...EducationalInfo },
        });
      } else {
        console.error('Failed to send data to the server');
        alert('Failed to submit data. Please try again.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred while submitting the form. Please try again later.');
    } finally {
      setSubmitting(false); // Set submitting state back to false after API call completes
    }
  };



    return (
      <div className="app">
        <PersonalInfo onSubmit={(data) => handleFormData(data, "personalInfo")} />
        <Address onSubmit={(data) => handleFormData(data, "address")} />
        <EducationalInfo onSubmit={(data) => handleFormData(data, "educationalInfo")} />
        <div className="s-btn">
          <label className="checkbox-label" htmlFor="termsCheckbox">
            <input
              id="termsCheckbox"
              type="checkbox"
              checked={termsAccepted}
              onChange={handleTermsChange}
            />
            <span className="custom-checkbox"></span>
            <span className="checkbox-text">Accept Terms and Conditions</span>
          </label>
          <button onClick={handleFinalSubmit} disabled={submitting}>
            {submitting ? 'Submitting...' : submitButtonText}
          </button>
        </div>
      </div>
    );
  };

  export default App;
