import React, { useState } from 'react';
import axios from 'axios';
import PersonalInfo from './components/PersonalInfo';
import Address from './components/Address';
import EducationalInfo from './components/EducationalInfo';
import './App.css';

const App = () => {
  const [formData, setFormData] = useState({
    personalInfo: {},
    address: {},
    educationalInfo: {},
  });

  const [formSubmitted, setFormSubmitted] = useState(false);
  const [termsCheckboxChecked, setTermsCheckboxChecked] = useState(false);

  const handlePersonalInfoSubmit = (data) => {
    console.log('Personal Info submitted:', data);
    setFormData((prevData) => ({ ...prevData, personalInfo: { ...prevData.personalInfo, ...data } }));
  };

  const handleAddressSubmit = (data) => {
    console.log('Address submitted:', data);
    setFormData((prevData) => ({ ...prevData, address: { ...prevData.address, ...data } }));
  };

  const handleEducationalInfoSubmit = (data) => {
    console.log('Educational Info submitted:', data);
    setFormData((prevData) => ({ ...prevData, educationalInfo: { ...prevData.educationalInfo, ...data } }));
  };

  const isPersonalInfoComplete = Object.values(formData.personalInfo).every((value) => value !== '');
  const isAddressComplete = Object.values(formData.address).every((value) => value !== '');
  const isEducationalInfoComplete = Object.values(formData.educationalInfo).every((value) => value !== '');

  const allSectionsComplete = isPersonalInfoComplete && isAddressComplete && isEducationalInfoComplete;

  const handleFinalSubmit = async () => {
    try {
      console.log('Handling final submit....');

      if (!termsCheckboxChecked) {
        alert('Please accept the Terms and Conditions.');
        return;
      }

      if (isPersonalInfoComplete && isAddressComplete && isEducationalInfoComplete) {
        const response = await axios.post('http://localhost:3001', formData);

        if (response.status === 200) {
          console.log('Form data sent to server successfully');
          setFormSubmitted(true);
        } else {
          console.error('Failed to send form data to server');
          alert('Failed to submit data. Please try again.');
        }
      } else {
        let missingFieldsMessage = 'Please fill the following sections:\n';
        if (!isPersonalInfoComplete) {
          missingFieldsMessage += '- Personal Information\n';
        }
        if (!isAddressComplete) {
          missingFieldsMessage += '- Address\n';
        }
        if (!isEducationalInfoComplete) {
          missingFieldsMessage += '- Educational Information\n';
        }

        alert(missingFieldsMessage);
      }
    } catch (error) {
      console.error('Error handling form submission:', error);
      alert('Failed to submit data. Please check you fill all required information.');
    }
  };

  return (
    <div className="App">
      <PersonalInfo onSubmit={handlePersonalInfoSubmit} />
      <Address onSubmit={handleAddressSubmit} />
      <EducationalInfo onSubmit={handleEducationalInfoSubmit} />

      <div className="s-btn">
        <label className="checkbox-label" htmlFor="termsCheckbox">
          <input
            id="termsCheckbox"
            type="checkbox"
            checked={termsCheckboxChecked}
            onChange={() => setTermsCheckboxChecked(!termsCheckboxChecked)}
          />
          <span className="custom-checkbox"></span>
          <span className="checkbox-text">Accept Terms and Conditions</span>
        </label>
        <button id="fsb" onClick={handleFinalSubmit} disabled={!allSectionsComplete}>
          {formSubmitted ? 'Data Submitted!' : 'Submit All Data'}
        </button>
      </div>
    </div>
  );
};

export default App;
